import React from 'react';
//import components
import Todo from "./Todo";

const TodoList = ({ todos, setTodos, filteredTodos }) => {

    const editTodo = id => {
        setTodos(todos.map(todo => todo.id === id ? {...todo, isEditing: !isEditing} : todo))
    }
    return (
        <div className="todo-container">
            <ul className="todo-list">
                {filteredTodos.map(todo => (
                    <Todo text={todo.text} key={todo.id} setTodos={setTodos} todos={todos} todo={todo} editTodo={editTodo} />
                ))}
            </ul>
        </div>
    )
}

export default TodoList;
