import React from 'react';
//import components
import Todo from "./Todo";

const TodoList = ({ todos, setTodos, filteredTodos }) => {

    const editTodo = id => {
        setTodos(todos.map(todo => todo.id === id ? {...todo, isEditing: !isEditing} : todo));
    }

    const editAllTodo = (todo, id) => {
        setTodos(todos.map(todo => todo.id === id ? {...todo, isEditing: !isEditing} : todo));
    }

    return (
        <div className="todo-container">
            <ul className="todo-list">
                {filteredTodos.map(todo => ( todo.isEditing ? (<EditTodoForm editTodo={editTodo} todo={todo} />) :
                    (<Todo text={todo.text} key={todo.id} setTodos={setTodos} todos={todos} todo={todo} editAllTodo={editAllTodo} />)
                ))}
            </ul>
        </div>
    )
}

export default TodoList;
